Ext.define('Ext.ux.data.Api', {

    constructor: function(config){
        var me = this;
        config = config || this.config || {};   
        function bind(item){
            var key, fn;
            if(item){
                if(Ext.isFunction(item)){
                    fn = item;
                    return function(){
                        return fn.apply(me, arguments);
                    };
                }else{
                    if(Ext.isObject(item)){
                        for(key in item){
                            if(item.hasOwnProperty(key)){
                                item[key] = bind(item[key]);     
                            }
                        }
                        return item;
                    }
                }
            }
        } 
        Ext.apply(this, bind(config));   
    },

    the: 'end'
});