/*global require, console */

Ext.define('demo2.MainServerApp', {
    extend: 'Ext.ux.app.Application',

    manager: {
        staticServer: 'demo2.manager.StaticServer'      
    },


    
    onBeforeInitialize: function(o){
 
        // add to bus
        this.bus.server = this.config.server;
        this.bus.express = this.config.express;
        
        // success
        this.handleSuccess(o);
    },


    the: 'end'
});